import React from "react";
import { useSelector, useDispatch, RootStateOrAny } from "react-redux";
import FontAwesome from "react-fontawesome";
import styled from "styled-components";
import search from "../search";

export default function Navbar() {
  const dispatch = useDispatch();
  const state = useSelector((state: RootStateOrAny) => state.hotelList);

  function handleChange(e: any) {
    const filteredData = search(e.target.value, state);
    dispatch({ type: "ADD_TO_DISPLAY", payload: filteredData });
  }
  return (
    <NavBarContainer>
      <div
        style={{
          display: "flex",
          width: "50%",
          justifyContent: "space-evenly",
        }}
      >
        <NavBarIcon>
          <FontAwesome name='building' size='2x' />
        </NavBarIcon>
        <NavBarInputBox>
          <NavBarInput
            onChange={handleChange}
            placeholder='Search'
          ></NavBarInput>
          <FontAwesome
            style={{ color: "gray" }}
            className='search'
            name='search'
          />
        </NavBarInputBox>
      </div>
      <Hamburger>
        <FontAwesome name='bars' size='2x' />
      </Hamburger>
      <NavBarLink>
        <NavLink>Become a Host</NavLink>
        <NavLink>Register</NavLink>
        <NavLink>Login</NavLink>
        <NavLink>Help</NavLink>
      </NavBarLink>
    </NavBarContainer>
  );
}

const NavBarContainer = styled.div`
  margin-bottom: 5px;
  margin-top: 15px;
  display: flex;
  padding: 10px;
`;
const NavBarInput = styled.input`
  width: 100%;
  padding: 10px;
  outline: none;
  box-shadow: 0 4px 8px 0 rgba(0, 0, 0, 0.2);
  border: 1px solid rgba(0, 0, 0, 0.3);
  :focus {
    outline: none;
    box-shadow: 0 0 5px #08aadb;
  }
`;
const NavBarIcon = styled.div`
  width: 20%;
  display: flex;
  justify-content: center;
  align-items: center;
`;
const NavBarInputBox = styled.div`
  position: relative;
  width: 80%;
  display: flex;
  justify-content: flex-end;
  align-items: center;
  .search {
    font-size: 1.5rem;
    position: absolute;
    padding: 0.5rem;
    right: 0;
  }
`;
const NavBarLink = styled.div`
  width: 50%;
  display: flex;
  justify-content: flex-end;
  align-items: center;
  @media (max-width: 900px) {
    display: none;
  }
`;
const NavLink = styled.p`
  margin: 0 20px;
  &:hover {
    cursor: pointer;
  }
`;
const Hamburger = styled.div`
  align-items: center;
  display: none;
  @media (max-width: 900px) {
    display: flex;
    width: 50%;
    justify-content: flex-end;
  }
`;
