import React from 'react'
import styled from 'styled-components'
import { RootStateOrAny, useDispatch, useSelector } from 'react-redux'
import HotelDetail from './HotelDetail'



function HotelList() {

  const dispatch = useDispatch();
  function handleClick() {
    dispatch({ type: 'ADD_TO_DISPLAY' })
  }
  const state = useSelector((state: RootStateOrAny) => {
    return { list : state.renderList, count : state.countOfHotel ,showMap:state.showMap}
  })
  const { list, count,showMap } = state;
  console.log(showMap)
  return (
    <HotelListDisplay toggle={showMap}>
      <CountDisplay>{count} places to stay</CountDisplay>
      {list.map((item:any) => {
        let hotelName = item.first_name + " " + item.last_name;
        return <HotelDetail title={hotelName} key={item.id}/>
      })}
      {list.length < count ? <DisplayMore onClick={handleClick}>More</DisplayMore> : ''}
    </HotelListDisplay>
  )
}

export default HotelList

const HotelListDisplay = styled.div<{toggle:Boolean}>`
display:flex;
flex-direction:column;
justify-content:space-evenly;
${((props) => props.toggle ? `
width:50%;
transition: all 300ms ease-out-in;
` : `width:100%;transition: all 300ms ease-in-out;`)}
`
const CountDisplay = styled.div`
margin-left:10px;
color:gray;
font-size:2rem;
font-weight:600;
`
const DisplayMore = styled.button`
width:50%;
margin : 10px auto;
padding:5px;
border:none;
outline:none;
cursor: pointer;
&:hover{
  background:#42adef;
}

`
