import React from 'react'
import styled from 'styled-components'
import { RootStateOrAny, useDispatch, useSelector } from 'react-redux'
import HotelDetail from './HotelDetail'
import axios from 'axios';



function HotelList() {

  const dispatch = useDispatch();
  function handleClick() {
    axios.get('https://reqres.in/api/users?page=2' ).then(response => {
      const response1 = response.data.data
      const data = [...response1]
      dispatch({ type: 'INITIALIZE', payload: data })
      dispatch({type:'ADD_TO_DISPLAY',payload:data})
      })
  }


  const state = useSelector((state: RootStateOrAny) => {
    return { list : state.renderList, count : state.countOfHotel ,showMap:state.showMap}
  })
  const { list, count,showMap } = state;

  return (
    <HotelListDisplay toggle={showMap}>
      <CountDisplay>{count} places to stay</CountDisplay>
      {list.map((item:any,idx:number) => {
        let hotelName = item.first_name + " " + item.last_name;
        return <HotelDetail  title={hotelName} key={idx}/>
      })}
      {list.length < count ? <DisplayMore onClick={handleClick}>More</DisplayMore> : ''}
    </HotelListDisplay>
  )
}

export default HotelList

const HotelListDisplay = styled.div<{toggle:Boolean}>`
height:80%;
overflow:scroll;
${((props) => props.toggle ? `
width:50%;
transition: all 300ms ease-out;
` : `width:100%;transition: all 300ms ease-in-out;`)}
`
const CountDisplay = styled.div`
margin-left:10px;
color:gray;
font-size:2rem;
font-weight:600;
`
const DisplayMore = styled.button`
width:100%;
margin : 10px auto;
padding:5px;
border:none;
outline:none;
cursor: pointer;
background : #cfeafb;
&:hover{
  background:#42adef;
}

`
