import React, { ReactElement } from "react";
import styled from "styled-components";

interface Props {}

function skeletonElement({}: Props): ReactElement {
  return (
    <Skeleton>
      <HotelImage />
      <HotelInfo>
        <Availability></Availability>
        <Title></Title>
        <Popularity>
          <img></img>
          <p>
            <span></span>
          </p>
        </Popularity>
        <RoomsDescription></RoomsDescription>
        <Address></Address>
        <Price></Price>
        <Rating></Rating>
      </HotelInfo>
    </Skeleton>
  );
}

export default skeletonElement;

const Skeleton = styled.div`
  display: flex;
  background: #ddd;
  width: 95%;
  box-shadow: 0 4px 8px 0 rgba(0, 0, 0, 0.2);
  padding: 7px;
  margin: 10px auto;
  border-radius: 5px;
  border: 1px solid rgba(0, 0, 0, 0.2);
`;

const Availability = styled.p`
  font-weight: 400;
`;
const Title = styled.h3`
  margin: 0;
  padding: 0 5px;
`;
const Popularity = styled.div`
  padding: 0 5px;
  margin: 5px 0;
  display: flex;
  color: #42adef;
  img {
    width: 30px;
    height: 30px;
  }
  span {
    font-weight: 800;
  }
  p {
    padding: 3px;
  }
  align-items: center;
`;
const RoomsDescription = styled.p`
  color: #1398eb; ;
`;
const Address = styled.p`
  font-size: 0.8em;
`;
const Price = styled.div`
  display: flex;
  width: 80px;
  color: #1398eb;
  margin-left: auto;
`;
const Rating = styled.p``;
const HotelImage = styled.img`
  width: 40%;
  object-fit: cover;
`;
const HotelInfo = styled.div`
  padding-left: 10px;
  width: 60%;
`;
