import React, { ReactElement, useState } from "react";
import {
  GoogleMap,
  InfoWindow,
  LoadScript,
  Marker,
} from "@react-google-maps/api";
import { data } from "../MarkerData";
import styled from "styled-components";
import { RootStateOrAny, useSelector } from "react-redux";
interface Props {}
const mapStyles = {
  height: "100%",
  width: "100%",
};

const defaultCenter = {
  lat: -37.78845,
  lng: 144.96268,
};
function Maps(props: Props): ReactElement {
  const state = useSelector((state: RootStateOrAny) => state.showMap);
  const [selected, setSelected] = useState<any>({});
  function handleSelect(item: any) {
    setSelected(item);
  }
  console.log(selected.name);
  return (
    <MapDisplay toggle={state}>
      <LoadScript googleMapsApiKey=''>
        <GoogleMap
          mapContainerStyle={mapStyles}
          zoom={4}
          center={defaultCenter}
          options={{ gestureHandling: "greedy" }}
        >
          {data.hotels.map((item) => {
            return (
              <Marker
                key={item.id}
                title={item.name}
                position={{ lat: +item.lat, lng: +item.lng }}
                onClick={() => handleSelect(item)}
                clickable={true}
              />
            );
          })}
          {selected.lat && (
            <InfoWindow
              position={{ lat: +selected.lat, lng: +selected.lng }}
              onCloseClick={() => setSelected({})}
            >
              <InfoBoxContainer>
                <InfoImage
                  src={
                    "http://www.jjtravelonline.com/images/inner-services/hotel-inner.png"
                  }
                />
                <InfoTitle>{selected.name}</InfoTitle>
                <Popularity>
                  <img
                    src={
                      "https://image.flaticon.com/icons/png/512/906/906338.png"
                    }
                    alt='feed'
                  ></img>
                  <div>
                    <span>RARE FIND</span> This place is usually booked.
                  </div>
                </Popularity>
                <RoomsDescription>
                  2 guests * 1 bedroom * 3 Beds * 1 Bath
                </RoomsDescription>
                <Address>{selected.address}</Address>
                <Price>${selected.price}</Price>
                <Rating>⭐️ (1) * Superhost</Rating>
              </InfoBoxContainer>
            </InfoWindow>
          )}
        </GoogleMap>
      </LoadScript>
    </MapDisplay>
  );
}

export default Maps;

const MapDisplay = styled.div<{ toggle: Boolean }>`
  width: ${(props) => (props.toggle ? "50%" : "0")};
`;

const InfoBoxContainer = styled.div`
  width: 220px;
  overflow: hidden;
`;
const InfoImage = styled.img`
  height: 150px;
  object-fit: cover;
`;
const InfoTitle = styled.div`
  font-size: 1rem;
  font-weight: 600;
`;
const Popularity = styled.div`
  padding: 0 3px;
  margin: 3px 0;
  display: flex;
  background: #cfeafb;
  color: #42adef;
  img {
    width: 20px;
    height: 20px;
  }
  span {
    font-weight: 600;
  }
  div {
    padding: 3px;
    display: flex;
  }
  align-items: center;
`;
const RoomsDescription = styled.div`
  color: #1398eb; ;
`;
const Address = styled.div`
  font-size: 0.6em;
`;
const Price = styled.div`
  display: flex;
  width: 80px;
  color: #1398eb;
  margin-left: auto;
`;
const Rating = styled.div``;
